INSERT INTO `prod_1peliculas` VALUES (213,'IM',NULL,NULL,NULL,'Aparecida, el milagro','Aparecida, o Milagre',2010,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El milagro de Aparecida cuenta la historia de Marcos, un empresario que vivió una infancia pobre en un lugar muy religioso del Brasil llamado Aparecida. A causa de una profunda frustración pierde la fe y ya adulto, se vuelca apasionadamente a la conquista del éxito material. Su hijo Lucas, con quien tiene muchos conflictos, sufre un grave accidente. Marcos, sumergido en la angustia de esta prueba, se ve apremiado por las circunstancias y desesperadamente enfrenta su conflicto de fe y pide a Nuestra Señora de Aparecida que le conceda el milagro de la vida de su único hijo.Un filme conmovedor que nos habla del encuentro de un hombre con su familia y consigo mismo, a través de la mediación materna de la Santísima Virgen María, Nuestra Señora de Aparecida, Protectora del Brasil. (Fuente: perpetuosocorrozac.org)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'2023-03-14 14:12:40',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,1,'2023-03-14 14:12:40',NULL,NULL,NULL);
