INSERT INTO `prod_3capitulos` VALUES (1432,92,1,14,'TMDB','340821',NULL,NULL,'Sombra de duda',NULL,1958,24,'US','en','Robert Stevenson','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, Myrna Fahey, George J. Lewis (Don Alejandro de la Vega)','Walt Disney Productions','Tan pronto como llega el nuevo comandante honesto, es asesinado por un misterioso extraño que incrimina a un anciano mendigo por el crimen y parece ser parte de una conspiración mayor.','https://image.tmdb.org/t/p/original/tvjdSiVNMJfOcV28hDIjHQtKK43.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
