INSERT INTO `prod_3capitulos` VALUES (1438,92,1,17,'TMDB','340824',NULL,NULL,'Dulce rostro del peligro',NULL,1958,24,'US','en','John Meredyth Lucas','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, George J. Lewis (Don Alejandro de la Vega)','Walt Disney Productions','Un día tranquilo en el pueblo cambia abruptamente cuando un extraño es asesinado por una flecha, aparentemente víctima de un ataque indígena. Diego ayuda al sargento García a inspeccionar el cuerpo en busca de pistas y encuentra otra misteriosa pluma de águila, esta vez en la falsa flecha india. Aparte de eso, no hay indicios de quién era el extraño o por qué fue asesinado.','https://image.tmdb.org/t/p/original/dX57NOiGE4gcd561PEePipE7VwX.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
