INSERT INTO `prod_3capitulos` VALUES (1440,92,1,18,'TMDB','340834',NULL,NULL,'El zorro se enfrenta a su padre',NULL,1958,24,'US','en','John Meredyth Lucas','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, Peter Brocco, Joan Shawlee, George J. Lewis (Don Alejandro de la Vega)','Walt Disney Productions','Una protesta pacífica de un grupo de peones molestos con los impuestos se torna violenta cuando uno de los hombres del Águila arroja una piedra por la ventana del magistrado. Galindo usa esto como excusa para ordenar severas medidas contra los campesinos, una medida que molesta a Don Alejandro. Los otros terratenientes quieren montar justicieros para castigar a los peones, pero Alejandro discute del lado de la razón.','https://image.tmdb.org/t/p/original/8QCAqIpOcc2VGO1F8sL2GTgERKC.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
