INSERT INTO `prod_3capitulos` VALUES (1444,92,1,20,'TMDB','340838',NULL,NULL,'Agente del águila',NULL,1958,24,'US','en','Norman Foster','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, Anthony Caruso','Walt Disney Productions','Don Alejandro le dice a Diego que recibió una carta de un amigo en la capital diciéndole que el nuevo comandante, Capitán Juan Ortega, tiene fama de ser un hombre honesto y justo. Cuando llega Ortega, no actúa así. Trata al sargento García con desprecio y es abiertamente arrogante con los civiles. El motivo de esto se revela cuando Ortega se encuentra con el magistrado y le informa que el comandante real ha sido emboscado y asesinado, por orden del Águila.','https://image.tmdb.org/t/p/original/egeOa6L1ASQQE7fAx7Mwsb7jHza.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
