INSERT INTO `prod_3capitulos` VALUES (1448,92,1,22,'TMDB','340832',NULL,NULL,'El desenmascaramiento del Zorro',NULL,1958,24,'US','en','Norman Foster','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, Anthony Caruso, George J. Lewis (Don Alejandro de la Vega)','Walt Disney Productions','Los planes del falso comandante y del magistrado se tuercen cuando Ortega ve a Diego hablando con Rosarita Cortez, una amiga de la infancia que acaba de regresar de la capital. Ortega teme que lo reconozca de su viaje, porque fue durante ese viaje que asesinó al verdadero Ortega y asumió su identidad. Bernardo escucha a los dos villanos que planean su muerte y notifica a Diego.','https://image.tmdb.org/t/p/original/pU9GBVOeMcX74Hs7171jbSykV3m.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
