INSERT INTO `prod_3capitulos` VALUES (1450,92,1,23,'TMDB','340841',NULL,NULL,'El secreto de la Sierra',NULL,1958,24,'US','en','Norman Foster','Johnston McCulley','William Lava','Guy Williams (Don Diego de la Vega / Zorro), Gene Sheldon (Bernardo), Henry Calvin (Demetrio López García), Vinton Hayworth, Rodolfo Acosta, George J. Lewis (Don Alejandro de la Vega)','Walt Disney Productions','Otro extraño con una pluma de águila significa problemas para el Zorro cuando Perico, un recién llegado, ve a una niña gitana tratando de pagar una compra con varias pepitas de oro. Diego y Alejandro se dan cuenta de que la noticia de un descubrimiento de oro provocará una oleada de \"fiebre del oro\" y su pacífica forma de vida se verá amenazada, por lo que tratan de suprimir la noticia.','https://image.tmdb.org/t/p/original/zVSW8e6Z0xobTdujksSRgdcq9Hi.jpg',0,0,0,1,1,NULL,1,1,1,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 19:48:48',2,'2023-05-24 02:36:25','2023-05-24 02:36:25',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:35:22',NULL,NULL,NULL);
