INSERT INTO `prod_3capitulos` VALUES (1484,96,1,2,'TMDB','86040',NULL,'tt0117927','Rebelión en las aulas 2','To Sir, with Love II',1996,92,'US','en','Peter Bogdanovich','E.R. Braithwaite, Philip Rosenberg','Trevor Lawrence','Sidney Poitier (Mark Thackeray), Christian Payton (Wilsie Carrouthers), Dana Eskelson (Evie), Fernando López (Danny Laredo), Casey Lluberes (Rebecca Torrado), Michael Gilio (Frankie Davanon), L.Z. Granderson (Arch Carrouthers), Bernadette Speakes (LaVerne Mariner), Jamie Kolacki (Stan Cameli), Saundra Santiago (Louisa Rodgriguez), Cheryl Lynn Bruce (Emily Taylor), Lulu (Barbara Pegg), Judy Geeson (Pamela Dare), Daniel J. Travanti (Horace Weaver)','Adelson-Baumgarten Productions, TriStar Television','Mark Thackeray regresa a Chicago, tras pasar 30 años en Londres dando clases en uno de los colegios británicos de mayor prestigio. En Chicago se enfrenta a un nuevo reto en su larga carrera como profesor de historia, ya que le toca una clase de chicos difíciles y desmotivados, más acostumbrados a la delincuencia que a los libros. (FILMAFFINITY)','https://image.tmdb.org/t/p/original/7cXcDQCjGLBIeOPdD3UcDzx60qx.jpg',0,0,0,1,1,NULL,1,1,54,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:33:56',2,'2023-05-24 02:33:34','2023-05-24 02:33:34',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:33:19',NULL,NULL,NULL);
INSERT INTO `prod_3capitulos` VALUES (1485,96,1,1,'TMDB','25934',NULL,'tt0062376','Rebelión en las aulas','To Sir, with Love',1967,105,'GB','en','James Clavell, Ted Sturgis','James Clavell, E.R. Braithwaite','Ron Grainer','Sidney Poitier (Mark Thackeray), Christian Roberts (Denham), Judy Geeson (Pamela Dare), Suzy Kendall (Gillian Blanchard), Lulu (Barbara \'Babs\' Pegg), Ann Bell (Mrs. Dare), Geoffrey Bayldon (Theo Weston), Faith Brook (Grace Evans), Patricia Routledge (Clinty Clintridge), Christopher Chittell (Potter), Lynne Sue Moon (Miss Wong), Adrienne Posta (Moira Joseph), Edward Burnham (Florian), Rita Webb (Mrs. Joseph), Fiona Duncan (Euphemia Phillips), Mona Bruce (Josie Dawes), Marianne Stone (Gert)','Columbia British Productions','Mark Thackeray, un ingeniero negro sin trabajo, acepta un empleo como profesor de un grupo de estudiantes bastante conflictivos en una escuela de la periferia de Londres. Sus alumnos son insolentes y groseros pero, en el fondo, no tienen malos sentimientos. Al principio intenta ganarse su confianza utilizando los métodos tradicionales, pero fracasa tan estrepitosamente que no tendrá más remedio que recurrir a otras fórmulas. (Fuente: TMDB)','https://image.tmdb.org/t/p/original/4Qq6JnZq6JSxRWWd6FsFTgbCmHq.jpg',0,0,0,1,1,NULL,1,1,54,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:33:56',2,'2023-05-24 02:33:34','2023-05-24 02:33:34',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:33:19',NULL,NULL,NULL);
INSERT INTO `prod_3capitulos` VALUES (1488,97,1,1,'TMDB','42390',NULL,'tt0029942','Forja de hombres','Boys Town',1938,96,'US','en','Norman Taurog, Horace Hough, Carl Roup','Dore Schary, Eleanore Griffin, John Meehan','Leonid Raab, Douglas Shearer, Leo Arnaud, Edward Ward, Paul Marquardt','Spencer Tracy (Father Flanagan), Mickey Rooney (Whitey Marsh), Henry Hull (Dave Morris), Leslie Fenton (Dan Farrow), Gene Reynolds (Tony Ponessa), Edward Norris (Joe Marsh), Addison Richards (Judge), Minor Watson (Bishop), Jonathan Hale (John Hargraves), Bobs Watson (Pee Wee), Martin Spellman (Skinny), Mickey Rentschler (Tommy Anderson), Frankie Thomas (Freddie Fuller), Jimmy Butler (Paul Ferguson), Sidney Miller (Mo Kahn), Robert Emmett Keane (Burton), Victor Kilian (The Sheriff)','Metro-Goldwyn-Mayer','Conmovido al escuchar la historia de un convicto que inició su carrera delictiva siendo un niño al que nadie dio una oportunidad, el padre Flanagan (Spencer Tracy) dedice construir la Ciudad de los Muchachos, un lugar donde los jóvenes conflictivos puedan convivir, trabajar y labrarse un futuro honrado. Allí acude a la fuerza Withey Marsh (Mickey Rooney), un joven rebelde que parece un caso perdido y que está decidido a seguir los pasos de su hermano Joe (Edward Norris), líder de una banda de delincuentes. (Fuente: TMDB)','https://image.tmdb.org/t/p/original/WmfioKiLTPp3ReC6nsgyc1mmxV.jpg',0,0,0,1,1,NULL,1,1,54,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:39:50',2,'2023-05-24 02:30:36','2023-05-24 02:30:36',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:27:25',NULL,NULL,NULL);
