INSERT INTO `prod_3capitulos` VALUES (1609,100,5,1,'TMDB','108130',NULL,NULL,'Mientras estemos juntos: Parte 1',NULL,1978,45,'US','en','Michael Landon','Michael Landon','David Rose','Melissa Gilbert (Laura Ingalls), Karen Grassle (Caroline Ingalls), Katherine MacGregor (Harriet Oleson), Melissa Sue Anderson (Mary Ingalls), Richard Bull (Nels Oleson), Lindsay Greenbush (Carrie Ingalls), Sidney Greenbush (Carrie Ingalls), Michael Landon (Charles Ingalls), Alison Arngrim (Nelly Olson), Katherine MacGregor (Harriet Olson), Anne Ramsey, Cletus Young, Leon Charles','Worldvision Enterprises, Ed Friendly Productions, NBC','Seguir viviendo en Walnut Grove es imposible y sus habitantes buscan una vida mejor en otro lugar. Los Ingalls se mudan a Winoka donde, gracias a Adam, Charles encuentra trabajo como gerente y Caroline de cocinera en el mismo hotel, y en el que se alojarán con las niñas. Aquí también llegan la familia Garvey y los Oleson, con la dificultad que comporta para todos el cambio de vida de la ciudad.','https://image.tmdb.org/t/p/original/qUcXtNaYYwxigwe7GXzeJQP3ovM.jpg',0,1,0,1,1,NULL,1,1,11,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:49:19',2,'2023-05-24 02:17:13','2023-05-24 02:17:13',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:16:52',NULL,NULL,NULL);
