INSERT INTO `prod_3capitulos` VALUES (1621,100,4,4,'TMDB','108042',NULL,NULL,'El carpintero',NULL,1977,45,'US','en','Blanche Hanalis, William F. Claxton, Maury Dexter, Victor French, Michael Landon, Leo Penn','Arthur Heinemann','David Rose','Melissa Gilbert (Laura Ingalls), Karen Grassle (Caroline Ingalls), Katherine MacGregor (Harriet Oleson), Melissa Sue Anderson (Mary Ingalls), Richard Bull (Nels Oleson), Lindsay Greenbush (Carrie Ingalls), Sidney Greenbush (Carrie Ingalls), Michael Landon (Charles Ingalls), Alison Arngrim (Nelly Olson), Katherine MacGregor (Harriet Olson), Hermione Baddeley, Gil Gerard, Ruth Foster','Worldvision Enterprises, Ed Friendly Productions, NBC','Charles interrumpe sus vacaciones, que aprovecha para arreglar la cocina de su mujer, para ir a entregar un importante pedido de maderas en su trabajo. Durante su ausencia Caroline contrata a un mafioso vagabundo para continuar las obras. Su integración en la familia Ingalls suscita comentarios en el pueblo y Mary teme que peligre la estabilidad familiar si se queda mas tiempo.','https://image.tmdb.org/t/p/original/tVrX8zkMq93t90RqYB1Mmz3M4F8.jpg',0,1,0,1,1,NULL,1,1,11,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:49:19',2,'2023-05-24 02:17:13','2023-05-24 02:17:13',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:16:52',NULL,NULL,NULL);
