INSERT INTO `prod_3capitulos` VALUES (1687,100,4,20,'TMDB','108061',NULL,NULL,'El regalo más precioso',NULL,1978,45,'US','en','Michael Landon','Laura Ingalls Wilder','David Rose','Melissa Gilbert (Laura Ingalls), Karen Grassle (Caroline Ingalls), Katherine MacGregor (Harriet Oleson), Melissa Sue Anderson (Mary Ingalls), Richard Bull (Nels Oleson), Lindsay Greenbush (Carrie Ingalls), Sidney Greenbush (Carrie Ingalls), Michael Landon (Charles Ingalls), Alison Arngrim (Nelly Olson), Katherine MacGregor (Harriet Olson), Hersha Parady','Worldvision Enterprises, Ed Friendly Productions, NBC','Los Ingalls están entusiasmados con la llegada de su quinto hijo, pero un comentario de Charles mal interpretado por Caroline, provoca que ella se obsesione con el sexo del bebé. Cuando la maestra, la Sra. Simms, da a luz un niño, aumenta su preocupación creyendo que le fallará si no le da el hijo varón que perdieron hace cuatro años.','https://image.tmdb.org/t/p/original/vTk5rojsMfIi1MtKp54IpMGCIo8.jpg',0,1,0,1,1,NULL,1,1,11,1,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,2,'2023-05-05 20:49:19',2,'2023-05-24 02:17:13','2023-05-24 02:17:13',96.00,NULL,NULL,NULL,NULL,NULL,3,NULL,2,'2023-05-24 02:16:52',NULL,NULL,NULL);
