INSERT INTO `prod_9edicion` VALUES (218,131,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Juana de Arco, la Doncella de Orleáns (1412-1431), una joven profundamente religiosa, vivió durante la Guerra de los Cien Años, en la que se enfrentaban Francia e Inglaterra. Completamente convencida de que Dios le había encomendado la misión de expulsar a los ingleses y de salvar a Francia, va a ver al Delfín, el futuro Carlos VII, y consigue que le proporcione tropas para levantar el sitio de Orleáns. (FILMAFFINITY)','1678627509430.jpg','nueva.jpg',1,1,0,1,1,NULL,71,NULL,NULL,NULL,NULL,NULL,1,'2023-03-12 12:56:22');
