INSERT INTO `prod_1peliculas` VALUES (76,'TMDB','255189',NULL,'tt2172049','Santa Barbara','Santa Barbara',2012,100,'IT','it','Carmine Elia','Maura Nuccetelli','','Vanessa Hessler (Barbara), Thomas Trabacchi (Marciano), Massimo Wertmüller (Dioscoro), Simone Montedoro (Claudio), Laura Glavan (Giuliana), Luciano Virgilio (Policarpo), Sergio Albelli (Tito), Ivan Alovisio (Livio)','RAI','\"Santa Bárbara\" narra la vida de la mártir que fue capaz de enfrentarse a su padre por la fe. La película, estrenada el 4 de diciembre de 2012 (festividad de la mártir), se convirtió en todo un éxito dando el salto a distintos países. El film está ambientado en el año 288 d.C., fecha que marcó un punto de inflexión en la historia: la libertad de la Iglesia en todo el imperio tras el edicto de Milán. Después de la muerte de su madre, Bárbara fue educada por su padre en la doctrina pagana. Un día su mejor amiga Juliana le habla sobre la fe cristiana y, tras descubrir que su propia madre también abrazó el cristianismo, Bárbara decide convertirse sin saber lo que su padre está dispuesto hacer para que cambie de parecer. (Fuente: TMDB)','https://image.tmdb.org/t/p/original/jeIIHxp6orfjXRheQ87uOT1pyqz.jpg',NULL,NULL,NULL,NULL,NULL,NULL,1,1,1,1,1,1,NULL,NULL,NULL,NULL,10,'2023-02-16 20:53:55',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
