INSERT INTO `prod_3capitulos` VALUES (23,6,1,2,'TMDB','58440',NULL,'tt0276572','Thomas','Amigos de Jesús: Tomás',2001,96,'IT','en','Raffaele Mertes, Elisabetta Marchetti','Gareth Jones','Marco Frisina','Ricky Tognazzi (Thomas), Maria Grazia Cucinotta (Maria Magdalena), Roberta Armani (Johanna), Mathieu Carriere (Pilatus), Enrica Maria Modugno (Maria), Danny Quinn (Jesus), Mehmet Günsür (Giovanni), Hannes Jaenicke (Joseph von Arimathaa), Mathias Herrmann (Longinus), Manfred Zapatka (Venturius), Matt Patresi (Barabba)',NULL,'La película muestra el impacto de Jesucristo a través de otra mirada en la que comprobamos el miedo de los apóstoles, sus conflictos y luchas bajo el yugo de la ocupación romana y sus vidas después de la ida del Mesías. La revelación del nacimiento de Cristo, y el milagro de su resurrección marcó el principio de una nueva era, pero son los discípulos de Jesucristo, los que extienden su mensaje a todos los confines del mundo. (FILMAFFINITY)','https://image.tmdb.org/t/p/original/r32jYzhOYfVo64qfotHeHEYF03b.jpg',1,1,0,NULL,NULL,5,1,1,1,1,1,1,NULL,NULL,NULL,NULL,2,'2023-01-02 01:34:35',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,0,NULL,NULL,NULL,NULL,NULL,NULL);
