INSERT INTO `prod_4edicion` VALUES (51,26,NULL,NULL,NULL,NULL,109,NULL,NULL,'US',NULL,'Brenda Chapman, Steve Hickner, Simon Wells','Philip LaZebnik, Nicholas Meyer','Desconocido','Dibujos animados','Dreamworks Pictures',NULL,NULL,NULL,1,1,0,1,2,2,61,NULL,NULL,10,'2023-01-06 04:29:15');
INSERT INTO `prod_4edicion` VALUES (52,NULL,9,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Roger Young','Lionel Chetwynd','Marco Frisina','Ben Kingsley, Frank Langella, Christopher Lee, Anna Galiena, Enrico Lo Verso, David Suchet, Maurice Roeves, Anthony Higgins, Philippe Leroy, Philip Stone, Anton Lesser, Anita Zagaria, Dudley Sutton','Coproducción Estados Unidos-España-Francia-Italia; TNT, Antena 3 Televisión, Beta Film GmbH,','Tras ser hallado en un cesto por una princesa egipcia de la familia real, Moisés, un niño judío, vivirá durante años disfrutando del lujo y los privilegios de la corte faraónica. Pero, cuando averigua cuál es su origen, abandona Egipto y se retira al desierto para meditar. Llega entonces a la conclusión de que su misión es liberar a los esclavos hebreos, pero esto lo enfrentará con el Faraón, que intentará por todos los medios evitar que Moisés saque a los judíos de Egipto para conducirlos a la Tierra Prometida. (FILMAFFINITY)','1672979575101.jpg',NULL,1,1,0,1,NULL,4,61,1,1,10,'2023-01-06 04:32:55');
