INSERT INTO `prod_4edicion` VALUES (57,34,NULL,NULL,NULL,NULL,45,NULL,NULL,NULL,NULL,NULL,NULL,'David Evans','Dibujos animados',NULL,'Moisés, quien fue recogido de una cesta que iba a la deriva en el río Nilo cuando era pequeño, huye de su malvado hermanastro Ramsés, y se marcha al desierto en compañía de un pájaro cantarín y algo escandaloso llamado Whistler. Su narra cómo salvó a su gente de la esclavitud y les condujo a la tierra de la leche y de la miel, abrió las aguas del Mar Rojo, y recibió las tablas de la ley con los Diez Mandamientos en el Monte Shaí. (FILMAFFINITY)',NULL,NULL,1,1,0,1,NULL,2,61,NULL,NULL,10,'2023-01-06 04:55:59');
