INSERT INTO `prod_4edicion` VALUES (91,63,NULL,NULL,NULL,NULL,80,NULL,NULL,NULL,NULL,NULL,NULL,'Oscar Martin Leanizbarrutia',NULL,'Goya Producciones','Mónica, una montadora de cine en busca de trabajo, recibe a través de una amiga el contacto de un productor que quiere editar un documental sobre las apariciones de Fátima. Aunque reticente por su agnosticismo, acepta. Mientras monta las imágenes desfilarán ante sus ojos sucesos extraordinarios que la inquietan. Al final, de modo súbito, su vida quedará marcada para siempre. (FILMAFFINITY)',NULL,'https://image.tmdb.org/t/p/original/fbsahnELMYI62Y1BZLlbBvToyJj.jpg',1,0,0,1,1,3,NULL,NULL,NULL,10,'2023-02-16 00:45:06');
