INSERT INTO `prod_4edicion` VALUES (125,NULL,45,NULL,NULL,NULL,70,NULL,NULL,NULL,NULL,'Cristian Mason','Desconocido',NULL,NULL,'Danae Gallegos Duk, Pilar Reynaldos','Narra la vida, obra y muerte del sacerdote jesuita chileno y fundador del \"Hogar de Cristo\", San Alberto Hurtado Cruchaga (encarnado por Cristián Campos), partiendo además de su vida, desde el proceso de beatificación y canonización, que posteriormente lo llevó a los altares. Basada principalmente en los documentos que se reunieron sobre el sacerdote por una comisión especial del arzobispado en la década de 1970, esta miniserie muestra otra faceta desconocida del Padre Hurtado: Su faceta de hombre trabajador e incansable, el hombre detrás del santo que hoy conocemos. (FilmAffinity)','1676602066354.jpg','https://image.tmdb.org/t/p/original/jkMQ2RZadZYsf8X3eoaNRRgew6m.jpg',1,1,0,1,1,3,69,NULL,NULL,10,'2023-02-17 02:47:46');
