INSERT INTO `prod_1peliculas` VALUES (205,'TMDB','199890',NULL,'tt2458586','Un Dios prohibido','Un Dios prohibido',2013,133,'ES','ES','Pablo Moreno, María Esparcia, Daniel Blasco, Anna Rodríguez','Juanjo Díaz Polo','Sergio Cardoso, Roberto Alonso, Idoia Iñarritu, Jofre Llivina','Iñigo Etayo (Ramón Illa), Eneko Capapay (Miguel Masip), Jacobo Muñoz (Eugenio Sopena), Ainhoa Aldanondo (Ángeles), Guido Balzaretti (Hall), Emma Caballero (Miliciana Montse), Chema Coloma, Elena Furiase (Trini), Jerónimo Salas (Faustino), Álex Larumbe (Juan Echarri), Ricardo del Cano (Atilio Parussini), Javier Suárez (Casadevall), Juan Lombardero (Vall), José María Rueda (Padre Juan Díaz), César Diéguez (Padre Sierra), Dani Gómez (Padre Masferrer), Jorge Ferrer (Simón)','Contracorriente Producciones SLU','Cuenta el martirio que 51 miembros de la Comunidad Claretiana de Barbastro (Huesca) sufrieron a manos de milicianos revolucionarios a comienzos de la Guerra Civil española (1936). Representa las últimas semanas de sus vidas antes de ser fusilados, gracias a los escritos originales que escribieron en este tiempo utilizados como testimonio para la elaboración del guión. En el momento a la Comunidad de Misioneros Claretianos de Barbastro pertenecían 9 sacerdotes, 12 hermanos y 39 seminaristas. Entre los últimos se encontraban dos de nacionalidad argentina a los que se puso en libertad, cuyas declaraciones fueron también esenciales para conocer lo que pasó dentro de la casa de la Comunidad Claretiana. (Fuente: TMDB)','1678801262310.jpg',1,1,1,0,0,1,5,1,56,52,1,1,'pst',3,0,2,2,2,0,2,2,2,0,NULL,NULL,NULL,NULL,628673,11,'2023-03-14 13:41:02',11,'2023-03-26 01:28:28','2023-03-26 01:28:28',96.00,11,'2023-06-09 01:47:12',1,'2023-03-14 13:41:02',11,'2023-03-26 01:26:30',9.13,NULL,NULL,NULL,NULL,3);
INSERT INTO `prod_1peliculas` VALUES (206,'TMDB','5653',NULL,'tt0309820','Lutero','Luther',2003,123,'DE US','EN','Eric Till','Camille Thomasson, Bart Gavigan','Richard Harvey, Michael Part','Joseph Fiennes (Martin Luther), Jonathan Firth (Girolamo Aleander), Claire Cox (Katharina von Bora), Alfred Molina (Johann Tetzel), Peter Ustinov (Frederick), Bruno Ganz (Johann von Staupitz), Uwe Ochsenknecht (Leo X), Maria Simon (Hanna), Mathieu Carriere (Cardinal Jacob Cajetan), Benjamin Sadler (Georg Spalatin), Jochen Horst (Andreas Karlstadt), Marco Hofschneider (Ulrich), Anian Zollner (Philipp von Hessen), Herb Andress (Gunter), Torben Liebrecht (Charles V)','ARD','Alemania, principios del XVI. Martín Lutero fue un monje agustino que provocó un cisma dentro de la Cristiandad. En el monasterio, una detenida y reflexiva lectura de la Biblia, lo lleva replantearse algunas cuestiones. En 1517, cuelga en la puerta de la iglesia de Wittenberg sus 95 tesis, que, además de negar algunos dogmas, condenaban las bulas de indulgencia que la Iglesia vendía para recaudar fondos para la construcción de la Basílica de San Pedro del Vaticano. Llevado ante la Dieta Imperial de Worms, Lutero se niega a retractarse, a menos que puedan probar, basándose en la Biblia, que sus tesis son falsas. A continuación es acusado de herejía y excomulgado por el Papa León X. En esta situación se ve obligado a escapar y vive como un proscrito mientras traduce el Nuevo Testamento del latín al alemán. La postura desafiante de Lutero anima a los campesinos a rebelarse contra los señores y a quemar iglesias. (FILMAFFINITY)','1678801505505.jpg',1,1,1,0,0,1,5,1,59,1,1,1,'pst',3,0,2,2,2,0,2,2,2,0,NULL,NULL,NULL,NULL,554191,11,'2023-03-14 13:45:05',11,'2023-03-24 19:23:41','2023-03-24 19:23:41',96.00,11,'2023-03-24 19:16:56',1,'2023-04-01 11:19:01',11,'2023-04-01 12:20:23',0.00,NULL,NULL,NULL,NULL,3);
