INSERT INTO `prod_3capitulos` VALUES (225,12,5,18,'TMDB','108150',NULL,NULL,'Las voces de los niños',NULL,1979,45,'US','EN','Blanche Hanalis, William F. Claxton, Maury Dexter, Victor French, Michael Landon, Leo Penn','Laura Ingalls Wilder','David Rose','Melissa Gilbert (Laura Ingalls), Karen Grassle (Caroline Ingalls), Katherine MacGregor (Harriet Oleson), Melissa Sue Anderson (Mary Ingalls), Richard Bull (Nels Oleson), Lindsay Greenbush (Carrie Ingalls), Sidney Greenbush (Carrie Ingalls), Michael Landon (Charles Ingalls), Alison Arngrim (Nelly Olson), Katherine MacGregor (Harriet Olson), Philip Abbott, Christopher Bowman','Worldvision Enterprises, Ed Friendly Productions, NBC','Mary comunica a su suegro su embarazo. Adam, distanciado de él desde su ceguera, se sorprende cuando viene a verlos. Dueño de un bufete de abogados en Nueva York, los convence para que se muden a su casa y que Adam estudie derecho para darle un futuro mejor a su nieto. Cuando el aborto de Mary le hace cambiar de opinión, ella se alegra de quedarse porque aquí tiene a su esposo, a su familia y a sus niños.','https://image.tmdb.org/t/p/original/h4715ZKTYsYj2bTtlkKdrPvuRbw.jpg',0,1,1,0,0,1,3,1,1,1,1,1,'pst',2,0,2,2,2,0,2,2,2,0,NULL,NULL,NULL,NULL,2,'2023-06-14 21:40:54',2,'2023-06-16 01:21:51','2023-06-16 01:21:51',27.68,2,'2023-06-16 01:00:32',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,3,3);
