INSERT INTO `prod_9edicion` VALUES (5,36,NULL,NULL,NULL,'Quédate un poco',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Ibrahim Maalouf',NULL,NULL,'Gad Elmaleh, afincado en Estados Unidos desde hace tres años, volvió a Francia sin atreverse a confesar ante su familia que lo hizo para convertirse al catolicismo. Cuando los padres de Gad, David y Régine, escuchan la noticia, es una pesadilla: su hijo no podría haberlo hecho peor. Decididos a devolver a Gad a su judaísmo, harán de su conversión un campo de batalla. Dios lo hará... (fuente: cine.com)','1673137412092.jpg','rsbger',0,0,1,0,NULL,NULL,NULL,NULL,NULL,11,NULL,NULL,NULL,11,'2023-01-08 00:23:32');
