INSERT INTO `prod_9edicion` VALUES (10,109,NULL,NULL,NULL,'Maria y José, una historia de fe',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El Arcángel Gabriel se le aparece a María y le dice que ella será la madre de Jesús, el hijo de Dios. Ella y su esposo José viajan a Belén, donde nace Jesús. (fuente: IMDB)','1677683397319.jpg','https://image.tmdb.org/t/p/original/oCUMW0rNyTjn7wgpeOQq7BDbi7z.jpg',1,1,1,0,NULL,1,3,14,NULL,NULL,NULL,NULL,'cnt',11,'2023-03-01 15:09:57');
