INSERT INTO `prod_9edicion` VALUES (71,487,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Desconocido',NULL,NULL,'Cuenta la historia de dos bandidos tan desalmados que osaron asaltar la mismísima basílica de Esquipulas. Pero una noche el líder de los delincuentes tiene un sueño, en el que se le aparece Jesús con la cruz al hombro. A la mañana siguiente los tesoros robados son devueltos y el ladrón asiste a misa. (FilmAffinity)','1698149651550.jpg','https://image.tmdb.org/t/p/original/yqPOC9q3H4sAsFWkOIsyMbcjAmZ.jpg',1,0,1,0,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,11,'2023-10-24 12:14:11');
INSERT INTO `prod_9edicion` VALUES (72,566,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Bill McAdams Jr., Grace McAdams','Roy Firestone',NULL,NULL,'Un acto de maldad acaba con las vidas de la esposa y los hijos de un hombre. Desesperado, se aísla del mundo y odia a Dios y la humanidad, hasta que dos niños le enseñan que el camino de la ira no cura las heridas del alma. (Film Affinity)','1704385601144.jpg','https://image.tmdb.org/t/p/original/mYUGrS1qxoiAiHZYqpVIOTOMyan.jpg',0,0,1,0,NULL,1,NULL,NULL,NULL,55,NULL,NULL,'pst',11,'2024-01-04 16:26:41');
